Cluster cluster = null;
Session session;

try {
    cluster = Cluster.builder().addContactPoint("127.0.0.1").build();
    session = cluster.connect("my_app");
    ResultSet resultSet = session.execute("select * from user;");


    for (Row row : resultSet) {
        System.out.format("Name is %s %s", row.getString("first_name"), row.getString("last_name"));
    }

    } finally {
        if (cluster != null) {
            cluster.close();
        }
    }
}

